var manual_volume= global.get("pump_manual_volume");
var flowrate= global.get("pump_flowrate");

if (manual_volume === undefined || manual_volume === "" || manual_volume === null) {
    msg.topic = "Missing entry :"
    msg.payload = "Volume to pass";
    return [null, msg];
}
else if (flowrate === undefined || flowrate === "" || flowrate === null) {
    msg.topic = "Missing entry :"
    msg.payload = "Flowrate";
    return [null, msg];
}
else {
    msg.topic = "actuator/pump";
    // msg.payload is FORWARD or BACKWARD here
    msg.payload={"action":"move", 
                "direction":msg.payload,
                "volume":manual_volume,
                "flowrate":flowrate};
}
return [msg, null];

var distance = global.get("focus_distance");
var speed = global.get("focus_speed");

if (distance === undefined || distance === "" || distance === null) {
    msg.topic = "Missing entry :"
    msg.payload = "Distance";
    return [null, msg]
}
if (speed === undefined || speed === "" || speed === null) {
    msg.topic = "Missing entry :"
    msg.payload = "Speed";
    return [null, msg]
}
// msg.payload is UP or DOWN here
msg.payload={"action":"move", 
            "direction":msg.payload,
            "distance":(distance/1000),
            "speed": (speed/1000)
};
return [msg, null];

let formDate = msg.payload.hardware_information.acq_planktoscope_date_factory;  // Access acq_planktoscope_date_factory in the nested object

// Check if the date is present and not an empty string
if (!formDate || formDate.trim() === "") {
    node.error("Date field is missing or undefined in payload");
    return null;  // Stop the flow if the date is undefined or empty
}

let parsedDate = new Date(formDate);  // Convert the ISO date to a Date object

// Check if the date is valid
if (isNaN(parsedDate.getTime())) {
    node.error("Invalid date: " + formDate);  // Log error if the date is invalid
    return null;  // Stop the flow if the date is invalid
}

// Extract the year, month, and day in YYYY/MM/DD format
let year = parsedDate.getFullYear().toString();  // Get the full year
let month = ('0' + (parsedDate.getMonth() + 1)).slice(-2);  // Ensure two digits for the month
let day = ('0' + parsedDate.getDate()).slice(-2);  // Ensure two digits for the day

// Format as 'YYYY/MM/DD'
msg.payload.hardware_information.acq_planktoscope_date_factory = `${year}/${month}/${day}`;

return msg;

