format:
#hardware-field {
    border: 1px solid;
    border-radius: 5px;
    width: 35vw;
    max-width: 500px;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    align-items: center;
}

#title-div {
    background-color: #87CEFA;
    width: 100%;
    padding: 10px;
    display: flex;
    align-items: center;
    justify-content: center;
}

h1 {
    color: white;
    text-align: center;
    margin: 0;
    font-size: 1.5rem;
}

#hardware-list {
    display: flex;
    flex-direction: column;
    gap: 15px;
    width: 100%;
    padding: 10px;
}

.field-container {
    display: flex;
    align-items: center;
    gap: 10px;
    width: 100%;
}

.field-container strong {
    flex: 0 0 45%;
    text-align: left;
    font-size: 1em;
}

.field-container span,
.field-container input {
    flex: 1;
    min-width: 130px;
    max-width: 200px;
    padding: 5px;
    height: 30px;
    border-radius: 4px;
    box-sizing: border-box;
}

.field-container .non-editable {
    background-color: #f5f5f5;
}

.field-container input {
    border: 1px solid #ccc;
}

.button-container {
    display: flex;
    gap: 5px;
}

button {
    padding: 5px;
}

.material-icons {
    vertical-align: middle;
    font-size: 1.2rem;
}

/* Media query for smaller screens */
@media (max-width: 600px) {
    .field-container {
        flex-direction: column;
        align-items: flex-start;
    }

    .field-container strong {
        width: auto;
    }

    .field-container span,
    .field-container input {
        width: 100%;
    }

    .button-container {
        justify-content: flex-start;
        width: 100%;
    }
}
